SELECT fu.uid, fu.name FROM facebook_users fu
	JOIN facebook_groups_users gu ON fu.uid = gu.uid
	JOIN facebook_groups fg ON gu.group_id = fg.group_id
	WHERE fg.group_id IN (358801190833478, 358739847506279, 617557238291204, 364881846892079, 358772894169641, 358728617507402, 362607020452895, 486526688060927, 715056245207969, 458414647538798, 676514102395517, 800678049979121, 561461737234088, 459523567427906, 787029648010628, 776153915764868, 681403838573210, 367996856580578, 359395334107397, 588169677896627, 358721727508091);